package model.buildings.generated;

import com.fasterxml.jackson.annotation.JsonCreator;
import com.fasterxml.jackson.annotation.JsonIgnore;
import com.fasterxml.jackson.annotation.JsonProperty;
import controller.GameManager;
import model.Coordinate;
import model.Person;
import model.enums.SaturationRate;
import util.Logger;

import java.util.ArrayList;

/**
 * This class represents a residential building that is generated by the game.
 */
public class ResidentialBuilding extends GeneratedBuilding {

    /**
     * Constructor of the residential building
     *
     * @param coords is the coordinates of the residential building
     */
    public ResidentialBuilding(Coordinate coords) {
        super(coords, GameManager.getFirePossibility(), false, null, 0);

        people = new ArrayList<>();
        publicSafety = 100;

        Logger.log("Residential building created at " + coords.toString());
    }

    @JsonCreator
    public ResidentialBuilding(@JsonProperty("coords") Coordinate coords, @JsonProperty("firePossibility") double firePossibility, @JsonProperty("isOnFire") boolean isOnFire, @JsonProperty("people") ArrayList<Person> people, @JsonProperty("saturationRate") SaturationRate saturationRate, @JsonProperty("publicSafety") int publicSafety) {
        super(coords, firePossibility, isOnFire, people, publicSafety);
    }

    public void addPerson(Person person) throws RuntimeException {
        if (people == null) {
            people = new ArrayList<>();
        }
        if (people.size() == maxCapacity) {
            Logger.log("Can't add person " + person.getName() + " to residential building at " + coords.toString() + " because maximum capacity reached!");
            throw new RuntimeException("Maximum capacity reached! Can't add new person!");
        } else {
            Logger.log("Person " + person.getName() + " added to residential building at " + coords.toString());

            people.add(person);
            person.setHome(this);
        }
    }

    public void removePerson(Person person) {
        Logger.log("Person " + person.getName() + " removed from residential building at " + coords.toString());

        people.remove(person);
        person.setHome(null);
    }

    @JsonIgnore
    @Override
    public String getStatistics() {
        String statistics = "Residential building statistics:\n";
        statistics += "Maximum capacity: " + maxCapacity + "\n";
        statistics += "Number of people living here: " + people.size() + "\n";
        statistics += "Public safety: " + publicSafety + "\n";
        return statistics;
    }

    @Override
    public void setOnFire() {
        Logger.log("Residential building set on fire at " + coords.toString());
        onFire = true;
    }

    @Override
    public void extinguish() {
        Logger.log("Residential building extinguished at " + coords.toString());
        onFire = false;
    }
}
